import { Diff } from "@/registry/ui/diff";
import { EXAMPLE_DIFF } from "./data";
import { CollapsibleCard as Card } from "@/registry/ui/collapsible-card";

export function DiffViewer() {
  return (
    <Card.Root
      data-section-id="diff-viewer"
      id="diff-viewer"
      className="my-8 text-[13px]"
    >
      <Card.Header>
        <Card.Title>File Changes</Card.Title>
      </Card.Header>
      <Card.Content>
        <Diff
          fileName="file-changes.tsx"
          patch={EXAMPLE_DIFF}
          status="modified"
          selectable={false}
        />
      </Card.Content>
    </Card.Root>
  );
}
